import React, { useState } from 'react';
import './App.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUpload, faPlay, faStar, faMoon, faSun } from '@fortawesome/free-solid-svg-icons';
import { motion } from 'framer-motion';

function App() {
  const [fileName, setFileName] = useState("No file selected");
  const [loading, setLoading] = useState(false);
  const [result, setResult] = useState(null);
  const [file, setFile] = useState(null);
  const [darkMode, setDarkMode] = useState(false);

  const handleFileChange = (e) => {
    const selected = e.target.files[0];
    if (selected) {
      setFile(selected);
      setFileName(selected.name);
      setResult(null);
    }
  };

  const handleAnalyze = async () => {
    if (!file) return;
    setLoading(true);
    const formData = new FormData();
    formData.append('file', file);

    try {
      const res = await fetch('http://localhost:5000/analyze', {
        method: 'POST',
        body: formData
      });
      const data = await res.json();
      setResult({
        bestMatch: {
          title: data.best_match.role,
          percentage: data.best_match.percentage
        },
        scores: data.match_percentages
      });
    } catch (err) {
      console.error(err);
    }
    setLoading(false);
  };

  return (
    <div className={darkMode ? "dark" : ""}>
      <nav className="navbar">
        <h1>Smart Analyzer</h1>
        <div className="nav-links">
          <button className="toggle-btn" onClick={() => setDarkMode(!darkMode)}>
            <FontAwesomeIcon icon={darkMode ? faSun : faMoon} />
          </button>
        </div>
      </nav>

      <motion.section className="hero-section" initial={{ opacity: 0, y: -30 }} animate={{ opacity: 1, y: 0 }} transition={{ duration: 0.6 }}>
        <h1><FontAwesomeIcon icon={faStar} /> Smart Resume Analyzer</h1>
        <p>Upload your resume and discover your best-fit tech roles instantly.</p>
      </motion.section>

      <motion.div className="upload-card" initial={{ opacity: 0, y: 30 }} animate={{ opacity: 1, y: 0 }} transition={{ delay: 0.2, duration: 0.6 }}>
        <label className="file-label">
          <input type="file" onChange={handleFileChange} />
          <FontAwesomeIcon icon={faUpload} size="2x" />
          <span>Select Resume</span>
          <em className="filename">{fileName}</em>
        </label>
      </motion.div>

      <motion.button className="analyze-button" onClick={handleAnalyze} disabled={!file} whileTap={{ scale: 0.95 }}>
        <FontAwesomeIcon icon={faPlay} /> {loading ? 'Analyzing...' : 'Analyze Resume'}
      </motion.button>

      {loading && <div className="loader"></div>}

      {result && (
        <motion.section className="results-section" initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ delay: 0.4, duration: 0.6 }}>
          <motion.div className="best-match-card" whileHover={{ y: -5 }} transition={{ duration: 0.3 }}>
            <h2>Top Match</h2>
            <p><strong>{result.bestMatch.title}</strong> ({result.bestMatch.percentage}%)</p>
          </motion.div>

          <motion.div className="match-list-card" initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ delay: 0.2, duration: 0.6 }}>
            <h3>Match Percentages</h3>
            <ul>
              {Object.entries(result.scores).map(([role, val]) => (
                <motion.li key={role} initial={{ opacity: 0, x: -20 }} animate={{ opacity: 1, x: 0 }} transition={{ duration: 0.5 }}>
                  <span className="role">{role}</span>
                  <div className="progress-bar"><div className="progress-fill" style={{ width: `${val}%` }}></div></div>
                  <span className="percent">{val}%</span>
                </motion.li>
              ))}
            </ul>
          </motion.div>
        </motion.section>
      )}
    </div>
  );
}

export default App;
