import React, { useState } from 'react';
import axios from 'axios';
import './App.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUpload, faCheckCircle, faLightbulb } from '@fortawesome/free-solid-svg-icons';

function App() {
  const [file, setFile] = useState(null);
  const [matchScores, setMatchScores] = useState({});
  const [bestMatch, setBestMatch] = useState(null);
  const [tips, setTips] = useState('');

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleAnalyze = async () => {
    if (!file) return;
    const formData = new FormData();
    formData.append('resume', file);

    try {
      const res = await axios.post('http://localhost:5000/analyze', formData);
      setMatchScores(res.data.match_scores);
      setBestMatch(res.data.best_match);
      setTips(res.data.tips);
    } catch (err) {
      console.error('Error analyzing resume:', err);
    }
  };

  return (
    <div className="app-container">
      <header>
        <h1><FontAwesomeIcon icon={faUpload} /> Smart Resume Analyzer</h1>
      </header>

      <div className="upload-box">
        <input type="file" onChange={handleFileChange} />
        <button onClick={handleAnalyze}>Analyze Resume</button>
      </div>

      {bestMatch && (
        <div className="result-card">
          <h2><FontAwesomeIcon icon={faCheckCircle} /> Best Match</h2>
          <p><strong>Role:</strong> {bestMatch.role}</p>
          <p><strong>Match:</strong> {bestMatch.score}%</p>
        </div>
      )}

      {tips && (
        <div className="tips-card">
          <h2><FontAwesomeIcon icon={faLightbulb} /> Tips to Improve</h2>
          <p>{tips}</p>
        </div>
      )}

      <div className="match-scores">
        <h2>Match Percentages</h2>
        {Object.entries(matchScores).map(([role, score]) => (
          <div className="score-bar" key={role}>
            <span>{role}</span>
            <div className="bar">
              <div className="fill" style={{ width: `${score}%` }}>{score}%</div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
