import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [file, setFile] = useState(null);
  const [matchResults, setMatchResults] = useState(null);
  const [bestMatch, setBestMatch] = useState(null);
  const [error, setError] = useState(null);

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
    setMatchResults(null);
    setBestMatch(null);
    setError(null);
  };

  const handleUpload = async () => {
    if (!file) return;
    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await axios.post('http://localhost:5000/analyze', formData);
      setMatchResults(response.data.match_percentages);
      setBestMatch(response.data.best_match);
      setError(null);
    } catch (err) {
      setError('Failed to analyze resume. Make sure backend is running.');
    }
  };

  return (
    <div className="app-container">
      <h1 className="title">Smart Resume Analyzer</h1>
      <div className="upload-section">
        <input type="file" accept="application/pdf" onChange={handleFileChange} />
        <button onClick={handleUpload}>Analyze Resume</button>
      </div>

      {error && <div className="error">{error}</div>}

      {bestMatch && (
        <div className="result-highlight">
          <h2>Best Match: <span>{bestMatch.role}</span> ({bestMatch.percentage}%)</h2>
        </div>
      )}

      {matchResults && (
        <div className="results">
          <h3>Match Percentages:</h3>
          <ul>
            {Object.entries(matchResults).map(([role, percentage]) => (
              <li key={role}>
                <strong>{role}</strong>: {percentage}%
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default App;
