import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [file, setFile] = useState(null);
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
    setResult(null); // reset on new file
  };

  const handleSubmit = async () => {
    if (!file) return;

    const formData = new FormData();
    formData.append('file', file);

    setLoading(true);
    try {
      const response = await axios.post('http://localhost:5000/analyze', formData);
      setResult(response.data);
    } catch (error) {
      console.error('Error analyzing resume:', error);
    }
    setLoading(false);
  };

  return (
    <div className="container">
      <h1><i className="fas fa-search"></i> Smart Resume Analyzer</h1>

      <div className="upload-box">
        <label htmlFor="file">
          <i className="fas fa-upload fa-2x"></i>
          <div>Choose File:</div>
          <em>{file ? file.name : 'No file selected'}</em>
        </label>
        <input type="file" id="file" onChange={handleFileChange} />
      </div>

      <button className="analyze-button" onClick={handleSubmit}>
        <i className="fas fa-play"></i>&nbsp; Analyze Resume
      </button>

      {loading && <div className="loader"></div>}

      {result && (
        <>
          <div className="best-match">
            <i className="fas fa-star"></i>
            <div>
              <div className="best-role">Best Match: <strong>{result.best_match.role}</strong></div>
              <div className="best-score">{result.best_match.percentage}% match</div>
            </div>
          </div>

          <div className="match-section">
            <h3>Match Percentages:</h3>
            <ul>
              {Object.entries(result.match_percentages).map(([role, percent]) => (
                <li key={role}>
                  <div className="role-name">{role}</div>
                  <div className="bar-container">
                    <div
                      className="bar-fill"
                      style={{
                        width: `${percent}%`,
                        backgroundColor:
                          percent >= 75
                            ? '#00b894'
                            : percent >= 50
                            ? '#fdcb6e'
                            : '#d63031'
                      }}
                    ></div>
                  </div>
                  <div className="percent">{percent}%</div>
                </li>
              ))}
            </ul>
          </div>
        </>
      )}
    </div>
  );
}

export default App;
